import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogList from "../../components/BlogList";
import Footer from "../../components/Footer";

import { useDispatch, useSelector } from "react-redux";
import { useParams, Link } from "react-router-dom";

// import "./index.css";

import blogService from "../../services/blogService";
import authService from "../../services/authService";
import AddEditBlogModal from "../../components/AddEditBlogModal";
import EditProfileModal from "../../components/EditProfileModal";
import Loading from "../../components/Loading";
import SuccessToast from "../../components/SuccessToast";
import ErrorToast from "../../components/ErrorToast";
import DeleteBlogModal from "../../components/DeleteBlogModal";

import { fetchAuthor, fetchBlogsByAuthorId, resetSuccessAndError, reset } from '../../features/authorSlice';

export default function ProfilePage() {
    const { authorId } = useParams();

    const dispatch = useDispatch();
    const {
      author, 
      authorBlogs,
      isError: isError,
      isSuccess: isSuccess,
      isLoading: isLoading,
      message: message,
    } = useSelector ((state) => state.author);

    useEffect(() => {
      dispatch(fetchAuthor(authorId));
      dispatch(fetchBlogsByAuthorId(authorId));
      return () => {
        dispatch(reset())
      }
    }, [authorId]);

    const AuthorDetails = (author) => {
      if (!author || author === "null" || author === "undefined") {
        return null;
      }

      return (
        <div className="author col-md-4">
          <div className="position-sticky my-5" style={{ top: "2rem" }}>
            <div className="p-4 mb-3 bg-light rounded">
              <h4 className="fst-italic">About the Author</h4>
              <h1>{author.firstName} {author.lastName}</h1>
              <p>{author.bio}</p>
              <img src={author.image} className="avatar" alt="Author" />
              <p>{author.bio}</p>
            </div>
          </div>
        </div>
      );
    }

    if (isLoading) {
      <Loading />
    }

    return (
      <>
        <Navbar />
        <div className="container">
          <AuthorDetails author={author} />
          <p className="page-subtitle">Author Blog Posts</p>
          <BlogList blogPosts={authorBlogs} />
          <Footer />
        </div>
        {/* <EditProfileModal /> */}
        <AddEditBlogModal />
        <DeleteBlogModal />
        <SuccessToast
          show={isSuccess}
          message={message}
          onClose={() => {dispatch(resetSuccessAndError())} }
        />
        <ErrorToast
          show={isError}
          message={message}
          onClose={() => {dispatch(resetSuccessAndError())} }
        />
      </>
    );
  }
