import React, { useEffect, useMemo } from 'react';

import { Modal } from 'bootstrap';

export default function AddEditBlogModal(addBlog) {

    const [blog, setBlog] = useState();

    const modalEl = document.getElementById("addEditModal")
    // const addEditModal = new Modal(addEditModalDom);
    const addEditModal = useMemo(() => {
        return modalEl ? new Modal(modalEl) : null;
      }, [modalEl]);

useEffect(() => {
    if (addBlog) {
        addEditModal.show();
    } 
}, [addBlog]);

    return (
        <div>
            {/* <!-- Button trigger modal --> */}
            {/* <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
            Launch demo modal
            </button> */}

            {/* <!-- Modal --> */}
            <div className="modal fade" id="addEditModal" tabindex="-1" aria-labelledby="addEditModalLabel" aria-hidden="true">
            <div className="modal-dialog">
                <div className="modal-content">
                <div className="modal-header">
                    <h1 className="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div className="modal-body">
                    ...
                </div>
                <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                    <button type="button" className="btn btn-primary">Save changes</button>
                </div>
                </div>
            </div>
            </div>
        </div>
    )
}
