import React from "react";
import BlogItem from "../BlogItem";
import PropTypes from "prop-types";
import { useNavigate } from "react-router-dom";
import "./index.css";

import { useDispatch } from "react-redux";

import {
  setDeleteBlog,
  setEditBlog,
} from "../../features/blogSlice";

export default function BlogList({ blogPosts }) {
  const dispatch = useDispatch();

  const nav = useNavigate();

  if (!blogPosts && !blogPosts?.length) {
    return null;
  }

  const onBlogEdit = (blog) => {
    dispatch(setEditBlog(blog));
  };

  const onBlogDelete = (blog) => {
    dispatch(setDeleteBlog(blog));
  };

  return (
    <div className="blog-list">
      {blogPosts.map((blogPost, index) => {
        return (
          <div onClick={() => nav("/blog/" + blogPost.id)}>
            <BlogItem
              key={index} 
              index={index}
              blogPost={blogPost}
              imageOrientation={"top"}
              setEditBlog={onBlogEdit}
              setDeleteBlog={onBlogDelete}
            />
          </div>
        );
      })}
    </div>
  );
}

BlogList.propTypes = {
  blogPosts: PropTypes.array.isRequired,
  setBlogEdit: PropTypes.func,
  setBlogDelete: PropTypes.func,
}
