const getBlogs = async () => {
  try {
    console.log("Trying to grab all Blogs")
    const data = await fetch(
      "https://ix-blog-app-2d5c689132cd.herokuapp.com/api/blogs",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

    let blogsApiData = await data.json();
    console.log(blogsApiData.message);
    return blogsApiData.data;
  } catch (error) {
    throw new Error(error);
  }
};

const blogsByCategoryId = async ( categoryId ) => {
  try {
    console.log("Trying to grab filtered blogs by cats ID")
    const data = await fetch(
      ("https://ix-blog-app-2d5c689132cd.herokuapp.com/api/blogs/category/6653a4471229714077337cfc"),
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

    let res = await data.json();
    console.log(res.message);
    return res.data;
  } catch(error) {
    throw new Error(error);
  }
}

  const blogService = {
    getBlogs, blogsByCategoryId
  };

  export default blogService;
