import React, {useDebugValue, useEffect, useState} from "react";

import Heading from "../../components/Heading";
import Navbar from "../../components/Navbar";
import BlogGrid from "../../components/BlogGrid";
import Footer from "../../components/Footer";
import SubHeading from "../../components/SubHeading";
import CategoryList from "../../components/CategoryList";
import Loading from "../../components/Loading";

import SuccessToast from "../../components/SuccessToast";
import ErrorToast from "../../components/ErrorToast";

import { useDispatch, useSelector } from "react-redux";
import { fetchBlogs, reset as resetBlogs } from "../../features/blogSlice";
import { fetchCategories, reset as resetCategories } from "../../features/categoriesSlice";

export default function Home() {
  const [isLoading, setIsLoading] = useState(false);

  const [isSuccess, setIsSuccess] = useState(false);
  const [isError, setIsError] = useState(false);

  const [message, setMessage] = useState("nada");

  const dispatch = useDispatch();
  const {
    blogs,
    isError: isBlogsError,
    isSuccess: isBlogsSuccess,
    isLoading: isLoadingBlogs,
    message: blogsMessage,
  } = useSelector((state) => state.blogs);
  const {
    categories, 
    isError: isCategoriesError, 
    isSuccess: isCategoriesSuccess,
    isLoading: isLoadingCategories,
    message: categoriesMessage,
  } = useSelector((state) => state.categories);

  useEffect(() => {
    dispatch(fetchCategories());
    dispatch(fetchBlogs());
    return () => {
      dispatch(resetBlogs());
      dispatch(resetCategories());
    };
  }, [dispatch]);

  const resetSuccess = () => {
    setIsSuccess(false);
    setMessage("nada");
  }

  const resetError = () => {
    setIsError(false);
    setMessage("nada");
  }

  if (isLoading) {
    return <Loading />;
  }

  return (
    <>
      <Navbar />
      <Heading />
       <div className="container">
        <SubHeading subHeading={"Recent Blog Posts"} />
        <BlogGrid blogPosts={blogs}></BlogGrid>
        <SubHeading subHeading={"Categories"} />
        <CategoryList categories={categories}></CategoryList>
        <Footer />
      </div>
      <SuccessToast 
        show={isBlogsSuccess || isCategoriesSuccess}
        message={blogsMessage || categoriesMessage}
        onClose={ () => {
          dispatch(resetBlogs());
          dispatch(resetCategories());
        }}
      />
      <ErrorToast
        show={isBlogsError || isCategoriesError}
        message={blogsMessage || categoriesMessage}
        onClose={ () => {
          dispatch(resetBlogs());
          dispatch(resetCategories());
        }}
      />
    </>
  );
}
