import React from "react";
import PropTypes from "prop-types";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

import BlogItemText from "../BlogItemText";
import EditButtons from "../EditButtons";

import "../../App.css";
import "./index.css";

export default function BlogItem({
    index,
    blogPost,
    imageOrientation,
    setEditBlog,
    setDeleteBlog,
}) {

    const user = JSON.parse(localStorage.getItem("user"));

    const nav = useNavigate();

    const navToBlog = () => {
      if ((!user && !user?.token) || (!setEditBlog && !setDeleteBlog)) {
        nav(`/blog/${blogPost.id}`);
      }
    };

    const EditButtonsContainer = () => {
      <EditButtons
        onEdit={() => setEditBlog(blogPost)}
        onDelete={() => setDeleteBlog(blogPost)}
        navToBlog={() => nav("/blog/" + blogPost.id)}
      />
    };


    if (imageOrientation === "top") {
        return (
          <div
            key={index}
            className="card-1"
            onClick={(navToBlog)}
          >
            <img src={blogPost.image} className="card-img-top" alt="..." />
            <div className="card-text-bottom">
              <BlogItemText blogPost={blogPost} />
              {user && user.token && setEditBlog && setDeleteBlog ? <EditButtonsContainer/> : null}
            </div>
          </div>
        );
    } else {
        return (
          <div
            key={index}
            className="card-2"
            onClick={navToBlog}
          >
            <img src={blogPost.image} className="card-img-left" alt="..." />
            <div className="card-text-right">
              {user && user.token && setEditBlog && setDeleteBlog ? <EditButtonsContainer /> : null}
              <BlogItemText blogPost={blogPost} />
            </div>
          </div>
        );
      }
  }
BlogItem.propTypes = {
  index: PropTypes.number.isRequired,
  blogPost: PropTypes.object.isRequired,
  imageOrientation: PropTypes.string,
  setEditBlog: PropTypes.func, 
  setBlogDelete: PropTypes.func,
}
