import React, { useEffect, useState } from 'react';

import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogList from "../../components/BlogList";
import Footer from "../../components/Footer";
// import CategoryList from '../../components/CategoryList';
// import SubHeading from '../../components/SubHeading';

import { useParams } from 'react-router-dom';

import "../../App.css";
import "./index.css";

//Importing Dummy Data
const data = require("../../dummy-data.json");
let blogsDummyData = data.blogPosts;
const categoriesDummyData = data.categories;

export default function BlogsPage() {
  let { categoryId } = useParams();

  // const [categoryId, setCategoryId] = useState( categoryIdPassed );
  const [blogs, setBlogs] = useState(blogsDummyData); 

  // blogsDummyData = blogsDummyData.filter((blog) => {
    //   return blog.categories.some((category) => category.id === categoryId)
    // })

  // const callBackFunction = () => {
  //   if (categoryId) {
  //     const filterBlogs = blogsDummyData.filter((blog) => {
  //       return blog.categories.some((category) => category.id === categoryId);
  //     });
  //     setBlogs(filterBlogs);
  //   }
  // }
  // useEffect(callBackFunction, [categoryId]);

  useEffect(() =>  {
    // filter blogposts based on categoryId
    const filteredBlogs = blogsDummyData.filter((x) => 
      categoryId !== undefined
        ? x.categories.find((y) => y.id.toString() === categoryId)
        : true
    );
    setBlogs(() => filteredBlogs);
  }, [categoryId]);

  const CategoriesList = ({ categoryId }) => {
    return categoriesDummyData.map((category) => {
      return categoryId === category.id ? (
        <button 
          key={category.id}
          onClick={() => setCategoryId(category.id)}
          style={{ color: "blue" }}
        >
          <p key={category.id}>{category.title}</p>
        </button>
      ) : (
        <button 
          key={category.id}
          onClick={() => setCategoryId(category.id)}
          style={{ color: "black" }}
          >
          <p key={category.id}>{category.title}</p> 
        </button>
      );
    });
  };

  return (
    <>
    <Navbar />
    <div className="container">
      <Heading />
      <div className="scroll-menu">
        <CategoriesList categoryId={categoryId} />
      </div>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <p className="page-subtitle">Blog Posts</p>
      </div>
      <BlogList blogPosts={blogs} />
    </div>

    <Footer />
  </>
  );
}
