import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';

import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogList from "../../components/BlogList";
import Footer from "../../components/Footer";
// import CategoryList from '../../components/CategoryList';
// import SubHeading from '../../components/SubHeading';

import blogService from "../../services/blogService";
import categoryService from '../../services/categoryService';

import "../../App.css";
import "./index.css";

//Importing Dummy Data
const data = require("../../dummy-data.json");
let blogsDummyData = data.blogPosts;
const categoriesDummyData = data.categories;

export default function BlogsPage() {
  // const nav = useNavigate();

  let { categoryIdParam } = useParams();
  // let [categoryId ] = useState(categoryId)

  // const [categoryId, setCategoryId] = useState( categoryIdParam );
  const [blogs, setBlogs] = useState( ); 
  const [categories, setCategories] = useState();

  // setLoading(False);

  // Week 1 ------------------------------------------------------------------
  // // filter blogposts based on categoryId
  // const filteredBlogs = blogsDummyData.filter((x) => 
  // categoryId !== undefined
  //     ? x.categories.find((y) => y.id.toString() === categoryId.toString())
  //     : 
  //     true
  // );
  // setBlogs(() => filteredBlogs);

  useEffect(() =>  {
    const fetchBlogs = async () => {
      try {
        const catRes = await categoryService.getCategories();
        const blogRes = await blogService.blogsByCategoryId(categoryIdParam || null);

        console.log("categoryId3", categoryIdParam)
        setBlogs(blogRes);
        setCategories(catRes);
      } catch (err) {
        console.log(err);
      }
    };
    fetchBlogs();
  }, [categoryIdParam]);



  const CategoriesList = ({ categoryId }) => {

    console.log("categoryID2", categoryId)
    console.log("categories", categories)

    if(!categories && !categories?.length){
      return null;
    }

    return categories.map((category, index) => {
      console.log("categoryID", categoryId)
      console.log("category.id", category.id)
      return categoryId === category.id ? (
        <Link
          key={index}
          // onClick={() => setLoading(true)}
          style={{ color: "blue" }}
          className='Link'
          to={"/blogs/"+category.id}
        >
          <p key={index}>{category.title}</p>
        </Link>
      ) : (
        <Link
          key={index}
          // onClick={() => setLoading(true)}
          to={"/blogs/"+category.id}
          style={{ color: "black" }}
          >
          <p key={index}>{category.title}</p> 
        </Link>
      );
    });
  };


  // if(loading){
  //   return(
  //     <div classname="d-flex justify-content-center align-items-center">
  //       <div class="spinner-border" role="status">
  //         <span class="visually-hidden">Loading...</span>
  //       </div>
  //     </div>
  //   )
  // }

  return (
    <>
    <Navbar />
    <div className="container">
      <Heading />
      <div className="scroll-menu">
        {console.log("CategoryID1", categoryIdParam)}
        <CategoriesList categoryId={categoryIdParam} />
      </div>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <p className="page-subtitle">Blog Posts</p>
      </div>
      <BlogList blogPosts={blogs} />
    </div>

    <Footer />
  </>
  );
}
