import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

import categoryService from "../services/categoryService";

const initialState = {
  addCategory: null,
  editCategory: null,
  deleteCategory: null,
  categories: [],
  isError: false,
  isSuccess: false,
  isLoading: false,
  message: "",
};

export const fetchCategories = createAsyncThunk(
  "categories/fetchCategories",
  async (_, thunkAPI) => {
    try {
      return await categoryService.fetchCategories();
    } catch (error) {
      const message = error.message || error;
      return thunkAPI.rejectWithValue(message);
    }
  }
);

export const updateCategory = createAsyncThunk(
  "categories/updateCategory",
  async(payload, thunkAPI) => {
    try {
      return await categoryService.updateCategory(payload);
    } catch (error) {
      const message = error.message || error;
      return thunkAPI.rejectWithValue(message);
    }
  }
)

export const categoriesSlice = createSlice({
  name: "categories",
  initialState,
  reducers: {
    reset: (state) => initialState,
    resetSuccessAndError: (state) => {
      state.isSuccess = false;
      state.isError = false;
      state.message = "";
    },
    setEditCategory: (state, {payload}) => {
      state.editCategory = payload;
      state.addCategory = null;
      state.deleteCategory = null;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchCategories.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(fetchCategories.fulfilled, (state, { payload }) => {
        state.categories = payload.data;
        state.isSuccess = true;
        state.isLoading = false;
        state.message = payload.message;
      })
      .addCase(fetchCategories.rejected, (state, { payload }) => {
        state.message = payload;
        state.isError = true;
        state.isLoading = false;
      })
      .addCase(updateCategory.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(updateCategory.fulfilled, (state, {payload}) => {
        const index = state.categories.map((x) => x.id === payload.data.id);
        state.categories = state.categories.map((x) => x.id !== payload.data.id);;
        state.categories.splice(index, 0, payload.data);
        state.editCategory = null;
        state.isSuccess = true;
        state.isError = false;
        state.isLoading = false;
        state.message = payload.message;
      })
      .addCase(updateCategory.rejected, (state, {payload}) => {
        // state.editCategory = null;
        state.isSuccess = false;
        state.isError = true;
        state.message = payload.message;
        state.isLoading = false;
      })
  },
});

export const { 
  reset, 
  resetSuccessAndError, 
  setEditCategory,
} = categoriesSlice.actions;
export default categoriesSlice.reducer;
