import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';

import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogList from "../../components/BlogList";
import Footer from "../../components/Footer";
import Loading from '../../components/Loading';
import AddEditBlogModal from '../../components/AddEditBlogModal';
import SuccessToast from '../../components/SuccessToast';
import ErrorToast from '../../components/ErrorToast';

import blogService from "../../services/blogService";
import categoryService from '../../services/categoryService';

import "../../App.css";
import "./index.css";

//Importing Dummy Data
const data = require("../../dummy-data.json");
let blogsDummyData = data.blogPosts;
const categoriesDummyData = data.categories;

export default function BlogsPage() {
  // const nav = useNavigate();

  let { categoryId } = useParams();
  // const categoryId = categoryPassed.id;

  // const [categoryId, setCategoryId] = useState( categoryIdParam );
  const [blogs, setBlogs] = useState([]); 
  const [categories, setCategories] = useState([]);
  const [addBlog, setAddBlog] = useState();
  const [isLoading, setIsLoading] = useState(false);

  const [ isError, setIsError ] = useState(false);
  const [ isSuccess, setIsSuccess ] = useState(false);
  const [ message, setMessage ] = useState("nada");

  // Week 1 ------------------------------------------------------------------
  // // filter blogposts based on categoryId
  // const filteredBlogs = blogsDummyData.filter((x) => 
  // categoryId !== undefined
  //     ? x.categories.find((y) => y.id.toString() === categoryId.toString())
  //     : 
  //     true
  // );
  // setBlogs(() => filteredBlogs);

  useEffect(() =>  {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const catRes = await categoryService.getCategories();
        let blogRes;
        if (categoryId) {
          blogRes = await blogService.fetchBlogsByCategoryId(categoryId);
        } else {
          blogRes = await blogService.fetchBlogs();
        }
        setCategories(catRes);
        setBlogs(blogRes.data.reverse());
        setIsSuccess(true);
        setMessage(blogRes.data.message);
        setIsLoading(false);
      } catch (error) {
        setIsError(true);
        setMessage(error.message || error);
        setIsLoading(false);
      }
    };
    fetchData();
  }, [categoryId]);

  const resetSuccess = () => {
    setIsSuccess(false);
    setMessage("nada");
  };

  const resetError = () => {
    setIsError(false);
    setMessage("nada");
  };

  const onBlogAddClicked = () => {
    setAddBlog({
      title: "",
      description: "",
      categories: [],
      author: {
        id: 1,
          firstName: "Byron",
          lastName: "de Villiers",
          bio: "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
          // image: "data:image/png;base64,"
      },
    })
  }

  const CategoriesList = ({ categoryId }) => {

    console.log("categoryID2", categoryId)
    console.log("categories", categories)

    if(!categories && !categories?.length){
      return null;
    }

    return categories.map((category) => {
      console.log("category.id", category.id)
      console.log("categoryID", categoryId)

      return categoryId === category.id ? (
        <Link
          className="link"
          key={category.id}
          style={{ color: "blue" }}
          to={"/blogs/" + category.id}
          onClick={() => setIsLoading(true)}
        >
          <p key={category.id}>{category.title}</p>
        </Link>
      ) : (
        <Link
          className="link"
          key={category.id}
          to={"/blogs/" + category.id}
          style={{ color: "black" }}
          onClick={() => setIsLoading(true)}
          >
          <p key={category.id}>{category.title}</p> 
        </Link>
      );
    });
  };

  if(isLoading){
    return <Loading />;
  }

  return (
    <>
      <Navbar />
      <div className="container">
        <Heading />
        <div className="scroll-menu">

          <CategoriesList categoryId={categoryId} />
        </div>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <p className="page-subtitle">Blog Posts</p>
          <AddEditBlogModal addBlog={addBlog} />
          <button 
            className='btn btn-outline-dark h-75'
            onClick={onBlogAddClicked()}
            >Add Blog</button>
        </div>
        <BlogList blogPosts={blogs} />
      </div>
      <Footer />
      <SuccessToast 
        show={isSuccess}
        message={message}
        onClose={resetSuccess}
      />
      <ErrorToast 
        show={isError}
        message={message}
        onClose={resetError}
      />
    </>
  );
}
