import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import CategoryList from '../../components/CategoryList';
import Footer from "../../components/Footer";
import Loading from "../../components/Loading";

import "../../App.css";
import SubHeading from '../../components/SubHeading';
import categoryService from '../../services/categoryService';
import SuccessToast from "../../components/SuccessToast";
import ErrorToast from "../../components/ErrorToast";
import AddEditCategoryModal from "../../components/AddEditCategoryModal";
import DeleteCategoryModal from "../../components/DeleteCategoryModal";

import { fetchCategories, resetSuccessAndError } from '../../features/categoriesSlice';
import { reset } from '../../features/blogSlice';

export default function CategoriesPage() {

  const dispatch = useDispatch();

  // const [categories, setCategories] = useState();
  // const [addCategory, setAddCategory ] = useState();
  // const [editCategory, setEditCategory] = useState();
  // const [deleteCategory, setDeleteCategory] = useState();

  const user = JSON.parse(localStorage.getItem("user"));

  const {
    categories,
    addCategory,
    deleteCategory,
    isLoading,
    isSuccess,
    isError,
    message,
  } = useSelector((state) => (state.categories))

  useEffect(() => {
    dispatch(fetchCategories());
    dispatch(resetSuccessAndError());
    return () => dispatch(resetSuccessAndError());
  }, []);

  const onCategoryAdd = () => {
    setAddCategory({
      title: "",
      description: "",
      color: "#000000",
    });
  };

  const onCategoryDelete = (category) => {
    setDeleteCategory(category);
  };

  const createCategory = async (category) => {
    try {
      const newCategory = await categoryService.createCategory(category);
      setIsSuccess(true);
      setMessage(newCategory.message);
      setCategories((prev) => {
        return [...prev, newCategory];
      });
    } catch (err) {
      setIsError(true);
      setMessage(err);
    }
    setAddCategory(null);
  };

  const updateCategory = async (category) => {
    dispatch(updateCategory(category));
    // try {
    //   const updatedCategory = await categoryService.updateCategory(category);
    //   setIsSuccess(true);
    //   setMessage(updatedCategory.message);
    //   setCategories((prev) => {
    //     const index = prev.findIndex((x) => x.id === updatedCategory.data.id);
    //     prev[index] = updatedCategory.data;
    //     return prev;
    //   });
    // } catch (err) {
    //   setIsError(true);
    //   setMessage(err);
    // }
    // setEditCategory(null);
  };

  const removeCategory = async (category) => {
    try {
      const newBlog = await categoryService.deleteCategory(category.id);
      setIsSuccess(true);
      setMessage(newBlog.message);
      setCategories((prev) => prev.filter((x) => x.id !== category.id));
    } catch (err) {
      setIsError(true);
      setMessage(err);
    }
    setDeleteCategory(null);
  };

  const AddButton = () => {
    if (!user?.token) return null;
    return (
      <button className="btn btn-outline-dark h-75" onClick={onCategoryAdd}>
        ADD CATEGORY
      </button>
    );
  };

  if (isLoading) {
    return <Loading />;
  }

  return (
    <>
      <Navbar />
      <div className="container">
        <Heading />
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <p className="page-subtitle">Categories</p>
          <AddButton />
        </div>
        <CategoryList
          categories={categories}
          onEdit={onCategoryUpdate}
          onDelete={onCategoryDelete}
        ></CategoryList>
      </div>
      <Footer />
      <AddEditCategoryModal
        addCategory={addCategory}
        editCategory={editCategory}
        createCategory={createCategory}
        updateCategory={updateCategory}
        onClose={() => {
          setAddCategory(null);
          setEditCategory(null);
        }}
      />
      <DeleteCategoryModal
        deleteCategory={deleteCategory}
        removeCategory={removeCategory}
        onClose={() => setDeleteCategory(null)}
      />
      <SuccessToast
        show={isSuccess}
        message={message}
        onClose={() => {
          setIsSuccess(false);
          setMessage("");
        }}
      />
      <ErrorToast
        show={isError}
        message={message}
        onClose={() => {
          setIsError(false);
          setMessage("");
        }}
      />
    </>
  );
}
