import React, { useEffect, useState } from 'react';

import Navbar from "../../components/Navbar";
import Heading from "../../components/Heading";
import BlogList from "../../components/BlogList";
import Footer from "../../components/Footer";
// import CategoryList from '../../components/CategoryList';
import SubHeading from '../../components/SubHeading';

import "../../App.css";
// import "./index.css";

//Importing Dummy Data
const data = require("../../dummy-data.json");
let blogsDummyData = data.blogPosts;
const categories = data.categories;

export default function BlogsPage() {
  const [blogs, setBlogs] = useState(blogsDummyData); 
  const [categoryId, setCategoryId] = useState([]);

  // blogsDummyData = blogsDummyData.filter((blog) => {
  //   return blog.categories.some((category) => category.id === categoryId)
  // })

  useEffect(() => {
    // filtering on blogposts based on categoryId
    const blogs = blogsDummyData.filter((blog) => 
      categoryId !== undefined ?
        blog.categories.find((category) => category.id.toString() === categoryId.toString())
        : true
    );
    setBlogs(() => blogs);
  }, [categoryId]);

  // const CategoriesList = ({ categoryid }) => {
  //   <buttone>
  //     key={index}
  //     onClick={() => setCategoryId(category.id)}
  //     style={{ color: "blue" }}
  //   >
  //     <p key={index}>{category.title</p>
  //   </buttone>
  // }

  return (
    <>
      <Navbar />
      <div className="container">
        <Heading />
        <div className="scroll-menu">
          {categories.map((category, index) => {
            return categoryId === category.id.toString() ? (
              <button
                key={index}
                onClick={() => setCategoryId(category.id)}
                style={{ color: "blue" }}
              >
                <p key={index}>{category.title}</p>
              </button>
            ) : (
              <button
                key={index}
                onClick={() => setCategoryId(category.id)}
                style={{ color: "black" }}
              >
                <p key={index}>{category.title}</p>
              </button>
            );
          })}
        </div>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <p className="page-subtitle">Blog Posts</p>
        </div>
        <BlogList setBlog={() => {}} blogPosts={blogs} />
      </div>
      <Footer />
    </>
  );
}
