const createCategory = async (category) => {
  console.log("You are creating a new category!")
  const response = await fetch(
    "http://localhost:8000/api/categories",
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: category
    });

  if (!response.ok) {
    try {
      let res = await response.json();
      throw res.message || console.log(res);
    } catch (err) {
      console.log(err);
      const error = new Error("Something went wrong");
      throw error.message;
    }
  }

  const responseData = await response.json();
  return responseData;
};

const fetchCategories = async () => {
    console.log("Trying to grab all categories")
    try {
        const data = await fetch(
          "http://localhost:8000/api/categories",
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
            },
          });

        let catsApiData = await data.json();
        console.log(catsApiData.message);
        return catsApiData.data;
      } catch (error) {
        throw new Error(error);
      }
    }

  const updateCategory = async (category) => {
    try {
      console.log("Updating a category!");
      const data = await fetch(
        "http://localhost:8000/api/categories/" + category.get("id"),
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: category
        });

      let res = await data.json();
      console.log(res.message);
      return res.data;
    } catch (error) {
      throw new Error(error);
    }
  };

  const deleteCategoryById = async (id) => {
    try {
      const data = await fetch(
        "http://localhost:8000/api/categories/" + id,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
          },
        });

      let res = await data.json();
      console.log(res.message);
      return res.data;
    } catch (error) {
      throw new Error(error);
    }
  };

  const categoryService = {
    createCategory,
    fetchCategories,
    updateCategory,
    deleteCategoryById,
  };

  export default categoryService;
